func TestAesDecrypt(t *testing.T) {
	password := "^%TytgUYGsefsq7JYU5F2OIJef"
	str := "A_sinishin@mail.ru"

	rows, err := AesEncrypt([]byte(str), []byte(password))
	if err != nil {
		t.Fatal("加密失败：", err)
	}
	//t.Log(hex.EncodeToString(rows))
	t.Log(hex.EncodeToString(rows))

	bytes, err := AesDecrypt(rows, []byte("^%TytgUYGsefsq7JYU5F2OIJef"))
	if err != nil {
		t.Fatal(err)
	}
	t.Log(string(bytes))
}

